import React, {useState} from 'react'
import {GoogleMap, withScriptjs, withGoogleMap, Marker, InfoWindow} from "react-google-maps"
import locationData from "../locationData.js"
import Nav from "./Nav"
import styled from "styled-components"

const Wrapper = sty;ed.div``

function Map() {

    const [selectedLocation, setSelectedLocation] = useState(null)

    return (
      <div>
        <GoogleMap
          defaultZoom={10}
          defaultCenter={{ lat: 39.748859, lng: -104.99752 }}
        >
          {locationData.map((loc) => (
            <Marker
              key={loc.id}
              position={{
                lat: loc.lat,
                lng: loc.lng,
              }}
              onClick={() => {
                setSelectedLocation(loc);
              }}
            />
          ))}
          {selectedLocation && (
            <InfoWindow
              position={{
                lat: selectedLocation.lat,
                lng: selectedLocation.lng,
              }}
              onCloseClick={() => setSelectedLocation(null)}
            >
              <div>
                <p>{selectedLocation.address}</p>
                <p>
                  {selectedLocation.city}. {selectedLocation.zip}
                </p>
                <p></p>
                <p>720-555-5555</p>
                <a href={selectedLocation.direction}>Directions</a>
              </div>
            </InfoWindow>
          )}
        </GoogleMap>
      </div>
    );
}

const WrappedMap = withScriptjs(withGoogleMap(Map))

export default function Contact(){
    return (
        <>
        <Nav />
      <div className="flex">
          <div className="info">
                <h1>Locations</h1>
          </div>
        <div style={{ width: "50vw", height: "100vh" }}>
          <WrappedMap
            googleMapURL={`https://maps.googleapis.com/maps/api/js?v=3.exp&libraries=geometry,drawing,places&key=AIzaSyC9JB8ol62HnhEKe6V1kNBSMao5UbmzV-o`}
            loadingElement={<div style={{ height: "100%" }} />}
            containerElement={<div style={{ height: "100%" }} />}
            mapElement={<div style={{ height: "100%" }} />}
          />
        </div>
      </div>
      </>
    );
}
