import React from "react"
import styled from "styled-components"

const Wrapper = styled.div`
  .wrap {
    display: table;
    width: 40%;
    height: 40%;
    margin-top: 8em;
  }
  form {
    height: 40%;
    width: 40%;
    display: table-cell;
    text-align: center;
    vertical-align: middle;
    background: linear-gradient(145deg, #cacccf, #ffffff);
    border-radius: 5%;
    box-shadow: 9.91px 9.91px 15px #d9dade, -9.91px -9.91px 15px #ffffff;
    padding: 3em;
  }

  input {
    width: 200px;
    height: 30px;

  }

  button {
    width: 200px;
    height: 30px;

  }
`;

const AdminForm =  (props) => {
    const {
        handleChange,
        handleSubmit,
        btnText,
        errMsg,
        inputs: {
            username, password
        }
    } = props

    return (
      <Wrapper>
        <div className="wrap">
          <form onSubmit={handleSubmit}>
            <input
              type="text"
              value={username}
              name="username"
              onChange={handleChange}
            />
            <br />
            <input
              type="password"
              value={password}
              name="password"
              onChange={handleChange}
            />
            <br />
            <button>{btnText}</button>
            <p>{errMsg}</p>
          </form>
        </div>
      </Wrapper>
    );
}

export default AdminForm
