import React, { useState, Component } from 'react'
import {Grid, Gif} from "@giphy/react-components"
import {GiphyFetch} from "@giphy/js-fetch-api"

const gf = new GiphyFetch("yw8cwBXgmbDqwljakyTvO2wNvqxRsIZN");


function Shoppingart() {

    function GridDemo({ onGifClick }) {
      const fetchGifs = (offset: number) =>
        GiphyFetch.trending({ offset, limit: 10 });
      return (
        <Grid
          onGifClick={onGifClick}
          fetchGifs={fetchGifs}
          width={800}
          columns={3}
          gutter={6}
        />
      );
    }


  const [modalGif, setModalGif] = useState();
  return (
    <>
      <img src="./logo.gif" width="200" alt="Powered by GIPHY" />
      <h4>Gif</h4>
      <GridDemo
        onGifClick={(gif, e) => {
          console.log("gif", gif);
          e.preventDefault();
          setModalGif(gif);
        }}
      />
      {modalGif && (
        <div
          style={{
            position: "fixed",
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            background: "rgba(0, 0, 0, .8)"
          }}
          onClick={e => {
            e.preventDefault();
            setModalGif(undefined);
          }}
        >
          <Gif gif={modalGif} width={200} />
        </div>
      )}
    </>
  );

}

export default ShoppingCart
