import React, {useState, useEffect} from "react"
import styled from "styled-components"
import {
  MDBContainer,
  MDBInputGroup,
  MDBSelect,
  MDBSelectInput,
  MDBSelectOptions,
  MDBSelectOption,
  MDBIcon
} from "mdbreact";

const Search = () => {

  const Container = styled.div`
    .selections {
      display: flex;
      justify-content: center;
    }
    select {
      width: 28% !important;
    }
    .selections {
      margin-top: 3rem;
    }
    .image {
      background: url("https://images.pexels.com/photos/1396122/pexels-photo-1396122.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=750&w=1260");
      height: 100vh;
      background-size: cover
    }
    .title{
      font-size: 4em
    }
  `;


    const [search, setSearch] = useState([])
    const [result, setResults] = useState('')
    const [query, setQuery] = useState({})

    const getProperty = async (e) => {
      e.preventDefault()
        const response = await fetch(
          "https://realtor.p.rapidapi.com/properties/v2/list-for-sale?sort=relevance&city=New%20York%20City&limit=200&offset=0&state_code=NY",
          {
            method: "GET",
            headers: {
              "x-rapidapi-host": "realtor.p.rapidapi.com",
              "x-rapidapi-key":
                "41f3f91b13mshaa0652a8c3b96f1p165abcjsn42e270cab71c",
            },
          }
        );
        const data = await response.json()
        setResults(data)
        console.log(data)
      }

      const updateSearch = (e) => {
        setSearch(e.target.value);
      };

      const getSearch = (e) => {
        e.preventDefault();
        setQuery(search);
        setSearch("");
      };

    return (
      <div>
        <Container>
          <div className=" image jumbotron card card-image">
            <div className="text-container">
              <h1 className="title text-center mt-5">Realtor App</h1>
              <MDBContainer className="d-flex">
                <MDBInputGroup
                  material
                  containerClassName="mb-2 mt-0 font"
                  prepend="City"
                  size="sm"
                />
                <MDBInputGroup
                  material
                  containerClassName="mb-2 mt-0 font"
                  prepend="State"
                  size="sm"
                />
              </MDBContainer>
              <div className="selections">
                <select className="browser-default custom-select w-25">
                  <option>
                    Bedrooms
                  </option>
                  <option value="1">1</option>
                  <option value="2">2</option>
                  <option value="3">3</option>
                  <option value="4">4</option>
                  <option value="5">5+</option>
                </select>
                <select className="browser-default custom-select w-25">
                  <option>Bathrooms</option>
                  <option value="1">1</option>
                  <option value="2">2</option>
                  <option value="3">3</option>
                  <option value="4">4</option>
                  <option value="5">5+</option>
                </select>
              </div>
            </div>
          </div>
        </Container>
      </div>
    );

}
export default Search
